  <script src="https://code.jquery.com/jquery-3.6.0.min.js" integrity="sha256-/xUj+3OJU5yExlq6GSYGSHk7tPXikynS7ogEvDej/m4=" crossorigin="anonymous"></script>
<script>
function removerItem(ID){

//localStorage.removeItem(ID);
$titulo = document.getElementById(ID).innerHTML;
 

          if ("PROD_"+$titulo in localStorage) {
		localStorage.removeItem("PROD_"+$titulo)
          } 
          if ("ORIG_"+$titulo in localStorage) {
		localStorage.removeItem("ORIG_"+$titulo)
          }
          if ("QNT_"+$titulo in localStorage) {
		localStorage.removeItem("QNT_"+$titulo)
          }  
location.reload();
   
}
  var $todos_limpos = [];
  var $somaNova= 0;
  var $precoTotal,$soma;
  var $arrayzinha = [];
  var $so_numero = [];
  var $so_titulo = [];
  var $carrinho = [];
  var $so_titulo_limpo = [];
  var $soma = 0;
  var $soma2 = 0;
  var $so_quantidades = [];
  var $so_qnt = [];
  var $so_preco_velho = [];
  var $so_preco_velho_numeros = [];

window.onload = function(){
  


// ACHA TODOS OS LOCALSTORAGE COM PREFIXO
    for (var i = 0; i < localStorage.length; i++){
      if (localStorage.key(i).substring(0,5) == 'PROD_') {
          $arrayzinha.push(localStorage.key(i));
          $arrayzinha.push(localStorage.getItem(localStorage.key(i)));        
      }
    }
    console.log($arrayzinha);
// ACHA TODAS AS QUANTIDADES;
for (var i = 0; i < localStorage.length; i++){
  
      if (localStorage.key(i).substring(0,4) == 'QNT_') {
          $so_quantidades.push(localStorage.key(i));
          $so_quantidades.push(localStorage.getItem(localStorage.key(i)));        
      }

    }
// ACHA TODOSO OS PREÃ‡OS VELHOS
for (var i = 0; i < localStorage.length; i++){
  
  if (localStorage.key(i).substring(0,5) == 'ORIG_') {
      $so_preco_velho.push(localStorage.key(i));
      $so_preco_velho.push(localStorage.getItem(localStorage.key(i)));        
  }

}
console.log('l');

  for($i = 0; $i < $so_preco_velho.length; $i++){
    if($so_preco_velho[$i].length <= 5){
          $so_preco_velho_numeros.push($so_preco_velho[$i]);
        }
  }
  console.log($so_preco_velho_numeros);

// DIVIDE EM DUAS ARRAYS 
    for($i = 0; $i < $arrayzinha.length; $i++){
      if($i % 2 == 0){
        $so_titulo.push($arrayzinha[$i]);
      }else{
        $so_numero.push($arrayzinha[$i]);
      }
    }
    $arrayFloat = $so_numero.map(Number);

    for($i = 0; $i < $so_quantidades.length; $i++){
      if($so_quantidades[$i].length <= 2){
        $so_qnt.push($so_quantidades[$i]);
      }
    }
   

// LIMPA O PREFIXO DOS TITULOS
var prefix = "PROD_";
for($i = 0; $i < $so_titulo.length; $i++){
   if ($so_titulo[$i].startsWith(prefix)) {
     $so_titulo_limpo.push($so_titulo[$i].slice(prefix.length));
    } 
}


    
    for (const numbers of $arrayFloat) { 
      $soma += numbers;
    }
    document.getElementById("totalH3").innerHTML =  'R$ '+ $soma.toFixed(2);

    for($i=0;$i<$so_titulo_limpo.length;$i++){
     // console.log($so_titulo_limpo[$i]);
      $carrinho.push($so_titulo_limpo[$i]);
        // document.getElementById("carrinho_boleto").value += $so_titulo_limpo[$i] + ", ";
        // document.getElementById("carrinho_credito").value += $so_titulo_limpo[$i] + ", ";
        // document.getElementById("carrinho_pix").value += $so_titulo_limpo[$i] + ", ";
console.log($carrinho);
        document.getElementById("carrinho_boleto").value = $carrinho;
        document.getElementById("carrinho_credito").value = $carrinho;
        document.getElementById("carrinho_pix").value = $carrinho;

      document.getElementById("valor_boleto").value = $soma;
      document.getElementById("valor_credito").value = $soma;
      document.getElementById("valor_pix").value = $soma;
      console.log($soma);
    }

    for($i = 0; $i< $so_titulo_limpo.length;$i++){
        var div = document.createElement("div");
        var h3 = document.createElement("h3");
        h3.innerHTML = $so_titulo_limpo[$i];
h3.id = $i;
        div.setAttribute('class', 'cada_produto');


    
        document.getElementById("container_produto").appendChild(div);
        div.appendChild(h3);
    }

    $array = document.querySelectorAll('.cada_produto');
    for($i = 0; $i< $array.length;$i++){
        var h3 = document.createElement("h3");
        h3.id = "cada"+$i;
        h3.className = "cadaprodutinho";
        $num = parseFloat($so_numero[$i]).toFixed(2);
        console.log($num);
      
       // h3.innerHTML = 'R$ ' + $so_numero[$i];
       h3.innerHTML = 'R$ ' + $num;

       $block_to_insert = document.createElement( 'div' );
       $block_to_insert.innerHTML = "<input type='number' onchange='atualiza_preco_unico_e_total(this.id)' class='qntds' name='qty' id='"+$i+"' min='1' max='10' step='1'>" ;



$block_to_insertt = document.createElement( 'div' );
$block_to_insertt.innerHTML ="<button id='"+$i+"' onclick='removerItem(this.id)'>X</button>";

        $array[$i].appendChild(h3);
        $array[$i].appendChild($block_to_insert);
	$array[$i].appendChild($block_to_insertt);

        //document.getElementById("container_produto").appendChild(div);
        //div.appendChild(h3);
    }

    $camp = document.querySelectorAll('.qntds');
    for(var i=0;i< $camp.length;i++){
        $camp[i].value = $so_qnt[i];
    }

   
}
function atualiza_preco_unico_e_total($id){
$todos_limpos = [];
$soma2 = 0;
  $valor = document.getElementById($id).value;

  $preco_original = $so_preco_velho_numeros[$id];
  $conta = $preco_original * $valor;
  //alert($conta.toFixed(2));
  console.log(document.getElementById("cada"+$id));
  document.getElementById("cada"+$id).innerHTML = "R$ "+$conta.toFixed(2);

  $todos = document.querySelectorAll('.cadaprodutinho');
  for($i = 0;$i<$todos.length;$i++){
    $item = $todos[$i].innerHTML.substring(3);
    $todos_limpos.push($item);
  }
  console.log("luiz");
console.log($todos_limpos);
$arrayFloat2 = $todos_limpos.map(Number);
// console.log($arrayFloat2);
for($g = 0;$g < $arrayFloat2.length;$g++){
  $soma2 += $arrayFloat2[$g];
}
    document.getElementById("totalH3").innerHTML =  'R$ '+ $soma2.toFixed(2);
document.getElementById('valor_boleto').value = $soma2.toFixed(2);
document.getElementById('valor_credito').value = $soma2.toFixed(2);
document.getElementById('valor_pix').value = $soma2.toFixed(2);
}

</script>




